'use client'

import { QueryClient, QueryClientProvider } from '@tanstack/react-query'
import { Toaster } from '@/components/ui/toaster'
import { TooltipProvider } from '@/components/ui/tooltip'
import { useState, ReactNode } from 'react'

export function Providers({ children }: { children: ReactNode }) {
  const [queryClient] = useState(() => new QueryClient())

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        {children}
      </TooltipProvider>
    </QueryClientProvider>
  )
}

components/audit-log-visualization.tsx.content:
import { useState, useEffect } from "react";
import { CheckCircle, AlertTriangle, Settings, Shield } from "lucide-react";

interface AuditLogItem {
  id: string;
  filename: string;
  status: 'scanning' | 'conflict' | 'fixing' | 'protected';
  timestamp: Date;
}

const statusConfig = {
  scanning: {
    icon: CheckCircle,
    color: 'text-green-500',
    bgColor: 'border-green-500/20',
    dotColor: 'bg-green-500',
    message: 'Scanned - Clean'
  },
  conflict: {
    icon: AlertTriangle,
    color: 'text-yellow-500',
    bgColor: 'border-yellow-500/20',
    dotColor: 'bg-yellow-500',
    message: 'Conflict Detected'
  },
  fixing: {
    icon: Settings,
    color: 'text-primary',
    bgColor: 'border-primary/20',
    dotColor: 'bg-primary',
    message: 'Auto-Fixing...'
  },
  protected: {
    icon: Shield,
    color: 'text-green-500',
    bgColor: 'border-green-500/20',
    dotColor: 'bg-green-500',
    message: 'Protected & Compliant'
  }
};

export default function AuditLogVisualization() {
  const [items, setItems] = useState<AuditLogItem[]>([]);

  useEffect(() => {
    const initialItems: AuditLogItem[] = [
      {
        id: '1',
        filename: 'contract_v2.docx',
        status: 'scanning',
        timestamp: new Date()
      },
      {
        id: '2', 
        filename: 'financial_report_Q3.xlsx',
        status: 'conflict',
        timestamp: new Date()
      },
      {
        id: '3',
        filename: 'financial_report_Q3.xlsx',
        status: 'fixing',
        timestamp: new Date()
      },
      {
        id: '4',
        filename: 'financial_report_Q3.xlsx', 
        status: 'protected',
        timestamp: new Date()
      }
    ];

    // Animate items appearing one by one
    initialItems.forEach((item, index) => {
      setTimeout(() => {
        setItems(prev => [...prev, item]);
      }, index * 500);
    });
  }, []);

  return (
    <div className="bg-background border border-border rounded-lg p-8 glow-border" data-testid="audit-log-visualization">
      <div className="space-y-4">
        {items.map((item, index) => {
          const config = statusConfig[item.status];
          const IconComponent = config.icon;

          return (
            <div
              key={item.id}
              className={`audit-log-item flex items-center justify-between p-4 bg-card rounded-lg border ${config.bgColor}`}
              style={{ animationDelay: `${index * 0.1}s` }}
              data-testid={`audit-log-item-${item.id}`}
            >
              <div className="flex items-center">
                <div className={`w-3 h-3 ${config.dotColor} rounded-full mr-4`}></div>
                <span className="font-mono text-sm" data-testid={`filename-${item.id}`}>
                  {item.filename}
                </span>
              </div>
              <div className={`flex items-center ${config.color}`}>
                <IconComponent className="mr-2 h-4 w-4" />
                <span className="text-sm" data-testid={`status-${item.id}`}>
                  {config.message}
                </span>
              </div>
            </div>
          );
        })}
      </div>

      <div className="mt-8 text-center">
        <p className="text-muted-foreground text-sm">
          <CheckCircle className="inline mr-2 h-4 w-4" />
          Real-time document protection and compliance monitoring
        </p>
      </div>
    </div>
  );
}

components/demo-form.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertDemoRequestSchema } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Calendar, CheckCircle } from "lucide-react";
import type { z } from "zod";

type DemoFormData = z.infer<typeof insertDemoRequestSchema>;

export default function DemoForm() {
  const [submitted, setSubmitted] = useState(false);
  const { toast } = useToast();

  const form = useForm<DemoFormData>({
    resolver: zodResolver(insertDemoRequestSchema),
    defaultValues: {
      email: "",
      company: "",
      teamSize: "",
    },
  });

  const onSubmit = async (data: DemoFormData) => {
    try {
      await apiRequest("POST", "/api/demo-request", data);
      setSubmitted(true);
      toast({
        title: "Demo request submitted!",
        description: "We'll be in touch within 24 hours to schedule your demo.",
      });
    } catch (error) {
      toast({
        title: "Error submitting request",
        description: "Please try again or contact support.",
        variant: "destructive",
      });
    }
  };

  if (submitted) {
    return (
      <div className="text-center p-8" data-testid="demo-form-success">
        <CheckCircle className="mx-auto h-16 w-16 text-green-500 mb-4" />
        <h3 className="text-2xl font-bold mb-2">Request Submitted!</h3>
        <p className="text-muted-foreground">
          We'll reach out within 24 hours to schedule your personalized demo.
        </p>
      </div>
    );
  }

  return (
    <div className="grid md:grid-cols-2 gap-12 items-center">
      <div>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6" data-testid="demo-form">
          <div>
            <Label htmlFor="email" className="block text-sm font-medium mb-2">
              Email Address
            </Label>
            <Input
              id="email"
              type="email"
              placeholder="your@company.com"
              {...form.register("email")}
              className="w-full bg-background border border-border rounded-lg px-4 py-3 focus:ring-2 focus:ring-primary focus:border-transparent"
              data-testid="input-email"
            />
            {form.formState.errors.email && (
              <p className="text-destructive text-sm mt-1">
                {form.formState.errors.email.message}
              </p>
            )}
          </div>

          <div>
            <Label htmlFor="company" className="block text-sm font-medium mb-2">
              Company
            </Label>
            <Input
              id="company"
              type="text"
              placeholder="Your Company"
              {...form.register("company")}
              className="w-full bg-background border border-border rounded-lg px-4 py-3 focus:ring-2 focus:ring-primary focus:border-transparent"
              data-testid="input-company"
            />
            {form.formState.errors.company && (
              <p className="text-destructive text-sm mt-1">
                {form.formState.errors.company.message}
              </p>
            )}
          </div>

          <div>
            <Label htmlFor="teamSize" className="block text-sm font-medium mb-2">
              Team Size
            </Label>
            <Select onValueChange={(value) => form.setValue("teamSize", value)}>
              <SelectTrigger 
                className="w-full bg-background border border-border rounded-lg px-4 py-3 focus:ring-2 focus:ring-primary focus:border-transparent"
                data-testid="select-team-size"
              >
                <SelectValue placeholder="Select team size" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="1-10">1-10 people</SelectItem>
                <SelectItem value="11-50">11-50 people</SelectItem>
                <SelectItem value="51-200">51-200 people</SelectItem>
                <SelectItem value="200+">200+ people</SelectItem>
              </SelectContent>
            </Select>
            {form.formState.errors.teamSize && (
              <p className="text-destructive text-sm mt-1">
                {form.formState.errors.teamSize.message}
              </p>
            )}
          </div>

          <Button 
            type="submit" 
            className="w-full bg-primary text-primary-foreground py-3 rounded-lg font-semibold hover:bg-primary/90 transition-colors"
            disabled={form.formState.isSubmitting}
            data-testid="button-schedule-demo"
          >
            <Calendar className="mr-2 h-4 w-4" />
            {form.formState.isSubmitting ? "Submitting..." : "Schedule Demo"}
          </Button>
        </form>
      </div>

      <div className="bg-background border border-border rounded-lg p-6" data-testid="calendly-placeholder">
        <div className="text-center text-muted-foreground">
          <Calendar className="mx-auto h-16 w-16 mb-4" />
          <h3 className="text-lg font-semibold mb-2">Choose Your Time</h3>
          <p className="text-sm mb-4">Available demo slots this week</p>
          <div className="space-y-2">
            <div className="bg-card p-3 rounded border text-left hover:bg-secondary cursor-pointer transition-colors">
              <span className="text-sm">Tomorrow 2:00 PM EST</span>
            </div>
            <div className="bg-card p-3 rounded border text-left hover:bg-secondary cursor-pointer transition-colors">
              <span className="text-sm">Tomorrow 3:30 PM EST</span>
            </div>
            <div className="bg-card p-3 rounded border text-left hover:bg-secondary cursor-pointer transition-colors">
              <span className="text-sm">Friday 4:00 PM EST</span>
            </div>
          </div>
          <p className="text-xs text-muted-foreground mt-4">
            Calendly integration will be embedded here
          </p>
        </div>
      </div>
    </div>
  );
}

hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

