import { useState, useEffect, useRef } from "react";
import { 
  Play, 
  Pause, 
  RotateCcw, 
  Upload, 
  FileText, 
  AlertTriangle, 
  CheckCircle, 
  Eye, 
  Zap, 
  Shield, 
  Clock, 
  DollarSign,
  TrendingUp,
  Settings,
  Search,
  Brain,
  Target
} from "lucide-react";
import { Button } from "@/components/ui/button";
