import { useState, useEffect } from 'react';
import { AlertTriangle, FileText, Eye, CheckCircle, Clock, DollarSign, Shield, TrendingUp, Filter, Search, Zap } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface ContradictionFinding {
  id: string;
  documentId: string;
  analysisId: string;
  contradictionType: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  title: string;
  description: string;
  pageNumber?: number;
  lineNumber?: number;
  textSnippet: string;
  potentialImpact: string;
  recommendation: string;
  suggestedFix: string;
  financialImpact?: string;
  preventedLoss?: string;
  status: 'detected' | 'reviewing' | 'resolved' | 'ignored';
  resolvedBy?: string;
  resolvedAt?: string;
  resolutionNotes?: string;
  createdAt: string;
  metadata?: {
    crossDocument?: boolean;
    documentIds?: string[];
    documentNames?: string[];
    textSnippets?: Array<{ documentId: string; snippet: string }>;
  };
}

interface Document {
  id: string;
  fileName: string;
  originalName: string;
  fileType: string;
  processingStatus: string;
  createdAt: string;
}

const severityConfig = {
  low: { color: 'text-green-500', bg: 'bg-green-500/20', label: 'Low', icon: 'üü¢' },
  medium: { color: 'text-yellow-500', bg: 'bg-yellow-500/20', label: 'Medium', icon: 'üü°' },
  high: { color: 'text-orange-500', bg: 'bg-orange-500/20', label: 'High', icon: 'üü†' },
  critical: { color: 'text-red-500', bg: 'bg-red-500/20', label: 'Critical', icon: 'üî¥' }
};

const contradictionTypeConfig = {
  budget: { icon: DollarSign, color: 'text-red-500', bg: 'bg-red-500/10', label: 'Budget Risk' },
  legal: { icon: Shield, color: 'text-purple-500', bg: 'bg-purple-500/10', label: 'Legal Issue' },
  compliance: { icon: Shield, color: 'text-blue-500', bg: 'bg-blue-500/10', label: 'Compliance' },
  version: { icon: AlertTriangle, color: 'text-yellow-500', bg: 'bg-yellow-500/10', label: 'Version Control' },
  deadline: { icon: Clock, color: 'text-orange-500', bg: 'bg-orange-500/10', label: 'Deadline Risk' },
  data: { icon: TrendingUp, color: 'text-indigo-500', bg: 'bg-indigo-500/10', label: 'Data Issue' }
};

export default function AuditDashboard() {
  const [searchTerm, setSearchTerm] = useState('');
  const [severityFilter, setSeverityFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedContradiction, setSelectedContradiction] = useState<ContradictionFinding | null>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch contradictions with filters
  const { data: contradictions, isLoading: contradictionsLoading } = useQuery({
    queryKey: ['/api/contradictions', { 
      severity: severityFilter, 
      status: statusFilter, 
      type: typeFilter,
      userId: 'demo-user' // TODO: Replace with actual user auth
    }],
    refetchInterval: 5000, // Refetch every 5 seconds for real-time updates
  });

  // Fetch documents for reference
  const { data: documents } = useQuery({
    queryKey: ['/api/documents'],
    queryParams: { userId: 'demo-user' }
  });

  // Resolve contradiction mutation
  const resolveMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes: string }) => {
      return apiRequest(`/api/contradictions/${id}/resolve`, {
        method: 'POST',
        body: JSON.stringify({ userId: 'demo-user', notes }),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: () => {
      toast({
        title: "Contradiction Resolved",
        description: "The contradiction has been marked as resolved.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/contradictions'] });
      setSelectedContradiction(null);
    },
    onError: (error: any) => {
      toast({
        title: "Resolution Failed",
        description: error.message || "Failed to resolve contradiction",
        variant: "destructive",
      });
    }
  });

  // Update contradiction status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      return apiRequest(`/api/contradictions/${id}`, {
        method: 'PUT',
        body: JSON.stringify({ status }),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: () => {
      toast({
        title: "Status Updated",
        description: "Contradiction status has been updated.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/contradictions'] });
    }
  });

  // Filter contradictions based on search and filters
  const filteredContradictions = contradictions?.filter((c: ContradictionFinding) => {
    const matchesSearch = !searchTerm || 
      c.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      c.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      c.textSnippet.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesSearch;
  }) || [];

  // Calculate statistics
  const stats = {
    total: filteredContradictions.length,
    critical: filteredContradictions.filter((c: ContradictionFinding) => c.severity === 'critical').length,
    high: filteredContradictions.filter((c: ContradictionFinding) => c.severity === 'high').length,
    resolved: filteredContradictions.filter((c: ContradictionFinding) => c.status === 'resolved').length,
    active: filteredContradictions.filter((c: ContradictionFinding) => c.status === 'detected').length,
  };

  const handleResolveContradiction = (contradiction: ContradictionFinding) => {
    const notes = `Resolved via audit dashboard at ${new Date().toISOString()}`;
    resolveMutation.mutate({ id: contradiction.id, notes });
  };

  const handleUpdateStatus = (id: string, status: string) => {
    updateStatusMutation.mutate({ id, status });
  };

  const getDocumentName = (documentId: string) => {
    const doc = documents?.find((d: Document) => d.id === documentId);
    return doc?.originalName || 'Unknown Document';
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  return (
    <div className="space-y-6" data-testid="audit-dashboard">
      {/* Dashboard Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Audit Dashboard</h1>
          <p className="text-muted-foreground">Real-time document contradiction analysis and compliance monitoring</p>
        </div>
        <div className="flex items-center gap-2">
          <Badge variant="outline" className="bg-green-500/10 text-green-500">
            <CheckCircle className="h-3 w-3 mr-1" />
            Live Monitoring
          </Badge>
        </div>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Total Issues</p>
                <p className="text-2xl font-bold">{stats.total}</p>
              </div>
              <AlertTriangle className="h-8 w-8 text-gray-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Critical</p>
                <p className="text-2xl font-bold text-red-500">{stats.critical}</p>
              </div>
              <span className="text-2xl">üî¥</span>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">High Risk</p>
                <p className="text-2xl font-bold text-orange-500">{stats.high}</p>
              </div>
              <span className="text-2xl">üü†</span>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Active</p>
                <p className="text-2xl font-bold text-blue-500">{stats.active}</p>
              </div>
              <Eye className="h-8 w-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Resolved</p>
                <p className="text-2xl font-bold text-green-500">{stats.resolved}</p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Filter className="h-5 w-5 mr-2" />
            Filters & Search
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <Input
                placeholder="Search contradictions..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full"
                data-testid="search-input"
              />
            </div>
            <Select value={severityFilter} onValueChange={setSeverityFilter}>
              <SelectTrigger className="w-[150px]">
                <SelectValue placeholder="Severity" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Severities</SelectItem>
                <SelectItem value="critical">Critical</SelectItem>
                <SelectItem value="high">High</SelectItem>
                <SelectItem value="medium">Medium</SelectItem>
                <SelectItem value="low">Low</SelectItem>
              </SelectContent>
            </Select>
            <Select value={typeFilter} onValueChange={setTypeFilter}>
              <SelectTrigger className="w-[150px]">
                <SelectValue placeholder="Type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Types</SelectItem>
                <SelectItem value="budget">Budget</SelectItem>
                <SelectItem value="legal">Legal</SelectItem>
                <SelectItem value="compliance">Compliance</SelectItem>
                <SelectItem value="version">Version</SelectItem>
                <SelectItem value="deadline">Deadline</SelectItem>
                <SelectItem value="data">Data</SelectItem>
              </SelectContent>
            </Select>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-[150px]">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value="detected">Detected</SelectItem>
                <SelectItem value="reviewing">Reviewing</SelectItem>
                <SelectItem value="resolved">Resolved</SelectItem>
                <SelectItem value="ignored">Ignored</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Contradictions List */}
      <Card>
        <CardHeader>
          <CardTitle>Contradiction Analysis Results</CardTitle>
        </CardHeader>
        <CardContent>
          {contradictionsLoading ? (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
              <span className="ml-2">Loading contradictions...</span>
            </div>
          ) : filteredContradictions.length === 0 ? (
            <div className="text-center py-8">
              <Shield className="h-12 w-12 mx-auto mb-4 text-green-500" />
              <p className="text-lg font-semibold text-green-600">No Issues Found</p>
              <p className="text-muted-foreground mt-1">
                {contradictions?.length === 0 
                  ? "Upload documents to start contradiction analysis" 
                  : "All contradictions are resolved or your filters exclude all results"
                }
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {filteredContradictions.map((contradiction: ContradictionFinding) => {
                const severityConf = severityConfig[contradiction.severity];
                const typeConf = contradictionTypeConfig[contradiction.contradictionType as keyof typeof contradictionTypeConfig];

                return (
                  <div
                    key={contradiction.id}
                    className="border rounded-lg p-4 hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                    data-testid={`contradiction-${contradiction.id}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          {typeConf && <typeConf.icon className={`h-4 w-4 ${typeConf.color}`} />}
                          <h3 className="font-semibold">{contradiction.title}</h3>
                          <Badge className={`${severityConf.bg} ${severityConf.color} border-0`}>
                            <span className="mr-1">{severityConf.icon}</span>
                            {severityConf.label}
                          </Badge>
                          {contradiction.metadata?.crossDocument && (
                            <Badge variant="default" className="bg-purple-500">
                              <Zap className="h-3 w-3 mr-1" />
                              Cross-Document
                            </Badge>
                          )}
                          <Badge variant="outline">
                            {contradiction.status}
                          </Badge>
                        </div>

                        {contradiction.metadata?.crossDocument && contradiction.metadata.documentNames ? (
                          <div className="text-sm text-muted-foreground mb-2">
                            <strong>Documents Involved:</strong>
                            <div className="ml-2 mt-1">
                              {contradiction.metadata.documentNames.map((name, index) => (
                                <div key={index} className="flex items-center">
                                  <span className="text-blue-500">‚Ä¢</span>
                                  <span className="ml-2">{name}</span>
                                </div>
                              ))}
                            </div>
                          </div>
                        ) : (
                          <p className="text-sm text-muted-foreground mb-2">
                            <strong>Document:</strong> {getDocumentName(contradiction.documentId)}
                            {contradiction.pageNumber && (
                              <span> ‚Ä¢ Page {contradiction.pageNumber}</span>
                            )}
                            {contradiction.lineNumber && (
                              <span> ‚Ä¢ Line {contradiction.lineNumber}</span>
                            )}
                          </p>
                        )}

                        <p className="text-sm mb-3">{contradiction.description}</p>

                        {contradiction.metadata?.crossDocument && contradiction.metadata.textSnippets ? (
                          <div className="mb-3">
                            <strong className="text-sm">Conflicting Text Snippets:</strong>
                            <div className="space-y-2 mt-2">
                              {contradiction.metadata.textSnippets.map((snippet, index) => (
                                <div key={index} className="bg-gray-100 dark:bg-gray-800 p-3 rounded text-sm">
                                  <div className="font-semibold text-purple-600 mb-1">
                                    {contradiction.metadata?.documentNames?.[index] || `Document ${index + 1}`}:
                                  </div>
                                  <div className="font-mono">"{snippet.snippet}"</div>
                                </div>
                              ))}
                            </div>
                          </div>
                        ) : contradiction.textSnippet && (
                          <div className="bg-gray-100 dark:bg-gray-800 p-3 rounded text-sm font-mono mb-3">
                            <strong>Text:</strong> "{contradiction.textSnippet}"
                          </div>
                        )}

                        <div className="grid md:grid-cols-2 gap-4 text-sm">
                          <div>
                            <strong className="text-red-500">Impact:</strong>
                            <p>{contradiction.potentialImpact}</p>
                          </div>
                          <div>
                            <strong className="text-blue-500">Recommendation:</strong>
                            <p>{contradiction.recommendation}</p>
                          </div>
                        </div>

                        {contradiction.suggestedFix && (
                          <div className="mt-3">
                            <strong className="text-green-600">Suggested Fix:</strong>
                            <p className="text-sm">{contradiction.suggestedFix}</p>
                          </div>
                        )}

                        {(contradiction.financialImpact || contradiction.preventedLoss) && (
                          <div className="mt-3 flex gap-4 text-sm">
                            {contradiction.financialImpact && (
                              <div className="text-red-600">
                                <strong>Financial Impact:</strong> {contradiction.financialImpact}
                              </div>
                            )}
                            {contradiction.preventedLoss && (
                              <div className="text-green-600">
                                <strong>Prevented Loss:</strong> {contradiction.preventedLoss}
                              </div>
                            )}
                          </div>
                        )}

                        <div className="mt-3 text-xs text-muted-foreground">
                          Created: {formatDate(contradiction.createdAt)}
                          {contradiction.resolvedAt && (
                            <span> ‚Ä¢ Resolved: {formatDate(contradiction.resolvedAt)}</span>
                          )}
                        </div>
                      </div>

                      <div className="flex gap-2 ml-4">
                        {contradiction.status === 'detected' && (
                          <>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => handleUpdateStatus(contradiction.id, 'reviewing')}
                              data-testid={`review-${contradiction.id}`}
                            >
                              Review
                            </Button>
                            <Button
                              size="sm"
                              onClick={() => handleResolveContradiction(contradiction)}
                              data-testid={`resolve-${contradiction.id}`}
                            >
                              <CheckCircle className="h-4 w-4 mr-1" />
                              Resolve
                            </Button>
                          </>
                        )}
                        {contradiction.status === 'reviewing' && (
                          <Button
                            size="sm"
                            onClick={() => handleResolveContradiction(contradiction)}
                            data-testid={`complete-${contradiction.id}`}
                          >
                            <CheckCircle className="h-4 w-4 mr-1" />
                            Complete
                          </Button>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/document-upload.ts.content:
import { useState, useCallback, useRef } from 'react';
import { Upload, X, FileText, AlertCircle, CheckCircle, Loader2, Eye } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface Document {
  id: string;
  fileName: string;
  originalName: string;
  fileType: string;
  fileSize: number;
  processingStatus: 'uploaded' | 'extracting' | 'analyzing' | 'completed' | 'failed';
  textExtractionStatus: 'pending' | 'processing' | 'completed' | 'failed';
  createdAt: string;
  processedAt?: string;
}

interface UploadedFile extends File {
  id: string;
  uploadProgress: number;
  status: 'pending' | 'uploading' | 'uploaded' | 'error';
  error?: string;
}

const getFileIcon = (fileType: string) => {
  switch (fileType.toLowerCase()) {
    case 'pdf':
      return 'üìÑ';
    case 'docx':
    case 'doc':
      return 'üìù';
    case 'txt':
      return 'üìã';
    default:
      return 'üìÑ';
  }
};

const getStatusColor = (status: string) => {
  switch (status) {
    case 'completed':
      return 'text-green-500';
    case 'analyzing':
    case 'extracting':
      return 'text-blue-500';
    case 'failed':
      return 'text-red-500';
    default:
      return 'text-gray-500';
  }
};

const getStatusBadge = (status: string) => {
  switch (status) {
    case 'uploaded':
      return <Badge variant="secondary">Uploaded</Badge>;
    case 'extracting':
      return <Badge variant="outline" className="text-blue-500"><Loader2 className="h-3 w-3 mr-1 animate-spin" />Extracting</Badge>;
    case 'analyzing':
      return <Badge variant="outline" className="text-blue-500"><Loader2 className="h-3 w-3 mr-1 animate-spin" />Analyzing</Badge>;
    case 'completed':
      return <Badge variant="default" className="bg-green-500">Completed</Badge>;
    case 'failed':
      return <Badge variant="destructive">Failed</Badge>;
    default:
      return <Badge variant="outline">Processing</Badge>;
  }
};

export default function DocumentUpload() {
  const [dragActive, setDragActive] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState<UploadedFile[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user's documents
  const { data: documents, isLoading: documentsLoading } = useQuery({
    queryKey: ['/api/documents'],
    queryParams: { userId: 'demo-user' } // TODO: Replace with actual user auth
  });

  // Upload mutation
  const uploadMutation = useMutation({
    mutationFn: async (files: File[]) => {
      const formData = new FormData();
      files.forEach((file) => {
        formData.append('documents', file);
      });
      formData.append('userId', 'demo-user'); // TODO: Replace with actual user auth

      // Use direct fetch for file uploads since apiRequest doesn't handle FormData properly
      const response = await fetch('/api/documents/upload', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        const text = await response.text() || response.statusText;
        throw new Error(`${response.status}: ${text}`);
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Upload Successful",
        description: data.message,
      });
      setSelectedFiles([]);
      queryClient.invalidateQueries({ queryKey: ['/api/documents'] });
    },
    onError: (error: any) => {
      toast({
        title: "Upload Failed",
        description: error.message || "Failed to upload documents",
        variant: "destructive",
      });
    },
  });

  // Drag and drop handlers
  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const droppedFiles = Array.from(e.dataTransfer.files);
    handleFiles(droppedFiles);
  }, []);

  const handleFiles = (files: File[]) => {
    const supportedTypes = ['application/pdf', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'application/msword', 'text/plain'];
    const maxSize = 10 * 1024 * 1024; // 10MB

    const validFiles: UploadedFile[] = [];
    const errors: string[] = [];

    files.forEach((file) => {
      if (!supportedTypes.includes(file.type) && !file.name.match(/\.(pdf|docx?|txt)$/i)) {
        errors.push(`${file.name}: Unsupported file type`);
        return;
      }

      if (file.size > maxSize) {
        errors.push(`${file.name}: File too large (max 10MB)`);
        return;
      }

      const uploadFile: UploadedFile = {
        ...file,
        id: `${Date.now()}-${Math.random()}`,
        uploadProgress: 0,
        status: 'pending'
      };

      validFiles.push(uploadFile);
    });

    if (errors.length > 0) {
      toast({
        title: "Invalid Files",
        description: errors.join(', '),
        variant: "destructive",
      });
    }

    setSelectedFiles(prev => [...prev, ...validFiles]);
  };

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      handleFiles(Array.from(e.target.files));
    }
  };

  const removeFile = (fileId: string) => {
    setSelectedFiles(prev => prev.filter(f => f.id !== fileId));
  };

  const handleUpload = async () => {
    if (selectedFiles.length === 0) return;

    setIsUploading(true);
    try {
      // Convert UploadedFiles back to regular Files for upload
      const filesToUpload = selectedFiles.map(f => new File([f], f.name, { type: f.type }));
      await uploadMutation.mutateAsync(filesToUpload);
    } catch (error) {
      // Error handled in mutation
    } finally {
      setIsUploading(false);
    }
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  return (
    <div className="space-y-6" data-testid="document-upload">
      {/* Upload Area */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Upload className="h-5 w-5 mr-2" />
            Upload Documents
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div
            className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors cursor-pointer
              ${dragActive 
                ? 'border-primary bg-primary/5' 
                : 'border-gray-300 dark:border-gray-700 hover:border-gray-400 dark:hover:border-gray-600'
              }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
            onClick={() => fileInputRef.current?.click()}
            data-testid="upload-area"
          >
            <Upload className="h-12 w-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-semibold mb-2">
              {dragActive ? 'Drop files here' : 'Upload your documents'}
            </h3>
            <p className="text-muted-foreground mb-4">
              Drag and drop files here, or click to browse
            </p>
            <p className="text-sm text-muted-foreground">
              Supports PDF, Word documents, and text files (max 10MB each)<br/>
              <span className="font-semibold text-primary">Upload dozens of files for cross-document contradiction analysis</span>
            </p>
            <input
              ref={fileInputRef}
              type="file"
              multiple
              accept=".pdf,.docx,.doc,.txt"
              onChange={handleFileInput}
              className="hidden"
              data-testid="file-input"
            />
          </div>

          {/* Selected Files */}
          {selectedFiles.length > 0 && (
            <div className="mt-6">
              <h4 className="font-medium mb-3">Selected Files ({selectedFiles.length})</h4>
              <div className="space-y-2">
                {selectedFiles.map((file) => (
                  <div
                    key={file.id}
                    className="flex items-center justify-between p-3 border rounded-lg"
                    data-testid={`selected-file-${file.id}`}
                  >
                    <div className="flex items-center flex-1">
                      <span className="text-2xl mr-3">{getFileIcon(file.name?.split('.')?.pop() || 'unknown')}</span>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium truncate">{file.name || 'Unknown file'}</p>
                        <p className="text-sm text-muted-foreground">{formatFileSize(file.size || 0)}</p>
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeFile(file.id)}
                      data-testid={`remove-file-${file.id}`}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>

              <div className="flex justify-end mt-4">
                <Button
                  onClick={handleUpload}
                  disabled={isUploading}
                  data-testid="upload-button"
                >
                  {isUploading ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Uploading...
                    </>
                  ) : (
                    <>
                      <Upload className="h-4 w-4 mr-2" />
                      Upload {selectedFiles.length} file{selectedFiles.length !== 1 ? 's' : ''}
                    </>
                  )}
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Documents List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span className="flex items-center">
              <FileText className="h-5 w-5 mr-2" />
              Your Documents
            </span>
            {documents && documents.length > 0 && (
              <Badge variant="secondary">{documents.length} documents</Badge>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {documentsLoading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-6 w-6 animate-spin mr-2" />
              Loading documents...
            </div>
          ) : documents && documents.length > 0 ? (
            <div className="space-y-3">
              {documents.map((document: Document) => (
                <div
                  key={document.id}
                  className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                  data-testid={`document-${document.id}`}
                >
                  <div className="flex items-center flex-1">
                    <span className="text-2xl mr-3">{getFileIcon(document.fileType)}</span>
                    <div className="flex-1 min-w-0">
                      <p className="font-medium truncate">{document.originalName}</p>
                      <div className="flex items-center gap-4 text-sm text-muted-foreground">
                        <span>{formatFileSize(document.fileSize)}</span>
                        <span>{formatDate(document.createdAt)}</span>
                        {document.processedAt && (
                          <span>Processed: {formatDate(document.processedAt)}</span>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-3">
                    {getStatusBadge(document.processingStatus)}
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => {
                        // TODO: Implement document view
                        toast({
                          title: "Document View",
                          description: "Document viewing will be implemented next",
                        });
                      }}
                      data-testid={`view-document-${document.id}`}
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <FileText className="h-12 w-12 mx-auto mb-4 text-gray-400" />
              <p className="text-muted-foreground">No documents uploaded yet</p>
              <p className="text-sm text-muted-foreground mt-1">
                Upload your first document to get started with AI-powered contradiction analysis
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

