app/api/checkout/route.ts.content:
import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import Stripe from 'stripe'

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, {
  apiVersion: '2024-06-20'
})

// Pricing configuration
const PRICING_CONFIG = {
  individual: {
    priceId: process.env.STRIPE_INDIVIDUAL_PRICE_ID || 'price_individual_default',
    amount: 4900, // $49.00
    name: 'Individual Plan',
    features: ['Up to 5 users', 'Basic discrepancy detection', 'Email support', 'Standard integrations']
  },
  pro: {
    priceId: process.env.STRIPE_PRO_PRICE_ID || 'price_pro_default', 
    amount: 9900, // $99.00
    name: 'Pro Plan',
    features: ['Up to 25 users', 'Advanced AI protection', 'Priority support', 'Custom workflows', 'Audit reporting']
  },
  team: {
    priceId: process.env.STRIPE_TEAM_PRICE_ID || 'price_team_default',
    amount: 39900, // $399.00
    name: 'Team Plan',
    features: ['Up to 100 users', 'Enterprise AI features', 'Dedicated support', 'Advanced integrations', 'SOC 2 compliance']
  }
}

export async function POST(request: NextRequest) {
  try {
    const supabase = createClient()

    // Check auth
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const body = await request.json()
    const { plan, successUrl, cancelUrl } = body

    if (!plan || !PRICING_CONFIG[plan as keyof typeof PRICING_CONFIG]) {
      return NextResponse.json({ error: 'Invalid plan selected' }, { status: 400 })
    }

    const selectedPlan = PRICING_CONFIG[plan as keyof typeof PRICING_CONFIG]

    // Check or create Stripe customer
    let customerId = ''

    // First check if user already has a Stripe customer ID
    const { data: profile } = await supabase
      .from('profiles')
      .select('stripe_customer_id')
      .eq('id', user.id)
      .single()

    if (profile?.stripe_customer_id) {
      customerId = profile.stripe_customer_id
    } else {
      // Create new Stripe customer
      const customer = await stripe.customers.create({
        email: user.email!,
        metadata: {
          supabase_user_id: user.id
        }
      })

      customerId = customer.id

      // Store customer ID in profile
      await supabase
        .from('profiles')
        .upsert({
          id: user.id,
          email: user.email!,
          stripe_customer_id: customerId
        })
    }

    // Create Stripe checkout session
    const session = await stripe.checkout.sessions.create({
      customer: customerId,
      payment_method_types: ['card'],
      line_items: [
        {
          price: selectedPlan.priceId,
          quantity: 1,
        },
      ],
      mode: 'subscription',
      success_url: successUrl || `${process.env.NEXT_PUBLIC_SITE_URL || 'http://localhost:3000'}/dashboard?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: cancelUrl || `${process.env.NEXT_PUBLIC_SITE_URL || 'http://localhost:3000'}/pricing`,
      allow_promotion_codes: true,
      billing_address_collection: 'required',
      metadata: {
        supabase_user_id: user.id,
        plan: plan
      },
      subscription_data: {
        metadata: {
          supabase_user_id: user.id,
          plan: plan
        }
      }
    })

    // Log checkout initiation
    const causalityId = crypto.randomUUID()
    await supabase
      .from('audit_log')
      .insert({
        causality_id: causalityId,
        action: 'checkout_initiated',
        details: {
          plan,
          sessionId: session.id,
          amount: selectedPlan.amount,
          customerId
        },
        user_id: user.id
      })

    return NextResponse.json({
      sessionId: session.id,
      url: session.url,
      plan: selectedPlan,
      causalityId
    })

  } catch (error) {
    console.error('Checkout error:', error)

    // Log checkout error
    const supabase = createClient()
    const { data: { user } } = await supabase.auth.getUser()

    if (user) {
      await supabase
        .from('audit_log')
        .insert({
          causality_id: crypto.randomUUID(),
          action: 'checkout_error',
          details: {
            error: error instanceof Error ? error.message : 'Unknown error'
          },
          user_id: user.id
        })
    }

    return NextResponse.json(
      { error: 'Failed to create checkout session' },
      { status: 500 }
    )
  }
}

// Handle successful checkout webhook
export async function PUT(request: NextRequest) {
  try {
    const body = await request.json()
    const { sessionId } = body

    if (!sessionId) {
      return NextResponse.json({ error: 'Session ID required' }, { status: 400 })
    }

    const supabase = createClient()

    // Retrieve session from Stripe
    const session = await stripe.checkout.sessions.retrieve(sessionId)

    if (session.payment_status === 'paid' && session.metadata?.supabase_user_id) {
      const userId = session.metadata.supabase_user_id
      const plan = session.metadata.plan

      // Update user profile with subscription info
      await supabase
        .from('profiles')
        .upsert({
          id: userId,
          subscription_status: 'active',
          subscription_plan: plan,
          stripe_subscription_id: session.subscription as string,
          updated_at: new Date().toISOString()
        })

      // Log successful subscription
      await supabase
        .from('audit_log')
        .insert({
          causality_id: crypto.randomUUID(),
          action: 'subscription_activated',
          details: {
            plan,
            sessionId,
            subscriptionId: session.subscription,
            amount: session.amount_total
          },
          user_id: userId
        })

      return NextResponse.json({ 
        success: true,
        plan,
        subscriptionId: session.subscription
      })
    }

    return NextResponse.json({ error: 'Payment not completed' }, { status: 400 })

  } catch (error) {
    console.error('Checkout confirmation error:', error)
    return NextResponse.json(
      { error: 'Failed to confirm payment' },
      { status: 500 }
    )
  }
}

app/api/discrepancies/route.ts.content:
import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'

export async function GET(request: NextRequest) {
  try {
    const supabase = createClient()

    // Check auth
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const { searchParams } = new URL(request.url)
    const projectId = searchParams.get('projectId')
    const severity = searchParams.get('severity')
    const type = searchParams.get('type')
    const page = parseInt(searchParams.get('page') || '1')
    const limit = parseInt(searchParams.get('limit') || '10')

    let query = supabase
      .from('discrepancies')
      .select(`
        *,
        projects!inner (
          user_id,
          name
        )
      `)
      .eq('projects.user_id', user.id)
      .order('created_at', { ascending: false })

    // Apply filters
    if (projectId) {
      query = query.eq('project_id', projectId)
    }

    if (severity) {
      query = query.eq('severity', severity)
    }

    if (type) {
      query = query.eq('type', type)
    }

    // Apply pagination
    const from = (page - 1) * limit
    const to = from + limit - 1
    query = query.range(from, to)

    const { data: discrepancies, error, count } = await query

    if (error) {
      console.error('Database query error:', error)
      return NextResponse.json({ error: 'Failed to fetch discrepancies' }, { status: 500 })
    }

    // Get counts by severity for summary
    const { data: summaryData } = await supabase
      .from('discrepancies')
      .select('severity, projects!inner(user_id)')
      .eq('projects.user_id', user.id)

    const severityCounts = summaryData?.reduce((acc, item) => {
      acc[item.severity] = (acc[item.severity] || 0) + 1
      return acc
    }, {} as Record<string, number>) || {}

    // Log access
    await supabase
      .from('audit_log')
      .insert({
        causality_id: crypto.randomUUID(),
        action: 'discrepancies_viewed',
        details: {
          filters: { projectId, severity, type },
          resultCount: discrepancies?.length || 0
        },
        user_id: user.id
      })

    return NextResponse.json({
      discrepancies: discrepancies || [],
      pagination: {
        page,
        limit,
        total: count || 0,
        totalPages: Math.ceil((count || 0) / limit)
      },
      summary: {
        total: summaryData?.length || 0,
        bySeverity: severityCounts
      }
    })

  } catch (error) {
    console.error('Discrepancies API error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

export async function POST(request: NextRequest) {
  try {
    const supabase = createClient()

    // Check auth
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const body = await request.json()
    const { discrepancyId, action, resolution } = body

    // Verify user owns this discrepancy
    const { data: discrepancy } = await supabase
      .from('discrepancies')
      .select(`
        *,
        projects!inner (
          user_id
        )
      `)
      .eq('id', discrepancyId)
      .eq('projects.user_id', user.id)
      .single()

    if (!discrepancy) {
      return NextResponse.json({ error: 'Discrepancy not found' }, { status: 404 })
    }

    // Log the action
    const causalityId = crypto.randomUUID()
    await supabase
      .from('audit_log')
      .insert({
        causality_id: causalityId,
        action: `discrepancy_${action}`,
        details: {
          discrepancyId,
          originalSeverity: discrepancy.severity,
          resolution: resolution || null
        },
        user_id: user.id,
        project_id: discrepancy.project_id
      })

    return NextResponse.json({
      success: true,
      action,
      causalityId
    })

  } catch (error) {
    console.error('Discrepancy action error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

app/api/upload/route.ts.content:
import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { parseDocument, detectDiscrepancies } from '@/lib/discrepancy'
import { v4 as uuidv4 } from 'uuid'
import pdfParse from 'pdf-parse'
import mammoth from 'mammoth'

export async function POST(request: NextRequest) {
  try {
    const supabase = createClient()

    // Check auth
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const formData = await request.formData()
    const files = formData.getAll('files') as File[]

    if (!files || files.length === 0) {
      return NextResponse.json({ error: 'No files provided' }, { status: 400 })
    }

    const projectId = uuidv4()
    const causalityId = uuidv4()

    // Create project record
    const { error: projectError } = await supabase
      .from('projects')
      .insert({
        id: projectId,
        name: `Upload ${new Date().toLocaleDateString()}`,
        user_id: user.id
      })

    if (projectError) {
      console.error('Project creation error:', projectError)
      return NextResponse.json({ error: 'Failed to create project' }, { status: 500 })
    }

    // Log upload start
    await supabase
      .from('audit_log')
      .insert({
        causality_id: causalityId,
        action: 'file_upload_started',
        details: {
          fileCount: files.length,
          filenames: files.map(f => f.name)
        },
        user_id: user.id,
        project_id: projectId
      })

    const parsedDocuments = []

    // Process each file
    for (const file of files) {
      try {
        const buffer = await file.arrayBuffer()
        let content = ''

        // Parse based on file type
        if (file.type === 'application/pdf') {
          const pdfData = await pdfParse(Buffer.from(buffer))
          content = pdfData.text
        } else if (file.name.endsWith('.docx')) {
          const result = await mammoth.extractRawText({ buffer: Buffer.from(buffer) })
          content = result.value
        } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
          content = new TextDecoder().decode(buffer)
        } else {
          throw new Error(`Unsupported file type: ${file.type}`)
        }

        // Store file in Supabase storage
        const fileName = `${projectId}/${uuidv4()}-${file.name}`
        const { error: uploadError } = await supabase.storage
          .from('documents')
          .upload(fileName, buffer, {
            contentType: file.type,
            metadata: {
              originalName: file.name,
              projectId: projectId
            }
          })

        if (uploadError) {
          console.error('File upload error:', uploadError)
          continue
        }

        // Parse document for discrepancies
        const parsedDoc = parseDocument(file.name, content)
        parsedDocuments.push(parsedDoc)

        // Log individual file processing
        await supabase
          .from('audit_log')
          .insert({
            causality_id: causalityId,
            action: 'file_processed',
            details: {
              filename: file.name,
              contentLength: content.length,
              entitiesCount: parsedDoc.entities.length,
              datesCount: parsedDoc.dates.length,
              amountsCount: parsedDoc.amounts.length
            },
            user_id: user.id,
            project_id: projectId
          })

      } catch (error) {
        console.error(`Error processing file ${file.name}:`, error)
        // Log processing error
        await supabase
          .from('audit_log')
          .insert({
            causality_id: causalityId,
            action: 'file_processing_error',
            details: {
              filename: file.name,
              error: error instanceof Error ? error.message : 'Unknown error'
            },
            user_id: user.id,
            project_id: projectId
          })
      }
    }

    // Detect discrepancies across all documents
    const discrepancies = detectDiscrepancies(parsedDocuments)

    // Store discrepancies in database
    if (discrepancies.length > 0) {
      const discrepancyRecords = discrepancies.map(d => ({
        type: d.type,
        file_a: d.fileA,
        value_a: d.valueA,
        file_b: d.fileB,
        value_b: d.valueB,
        severity: d.severity,
        project_id: projectId
      }))

      const { error: discrepancyError } = await supabase
        .from('discrepancies')
        .insert(discrepancyRecords)

      if (discrepancyError) {
        console.error('Discrepancy storage error:', discrepancyError)
      }
    }

    // Log completion with results
    await supabase
      .from('audit_log')
      .insert({
        causality_id: causalityId,
        action: 'upload_completed',
        details: {
          projectId,
          filesProcessed: parsedDocuments.length,
          discrepanciesFound: discrepancies.length,
          severityBreakdown: discrepancies.reduce((acc, d) => {
            acc[d.severity] = (acc[d.severity] || 0) + 1
            return acc
          }, {} as Record<string, number>)
        },
        user_id: user.id,
        project_id: projectId
      })

    return NextResponse.json({
      success: true,
      projectId,
      filesProcessed: parsedDocuments.length,
      discrepanciesFound: discrepancies.length,
      discrepancies: discrepancies.slice(0, 10), // Return first 10 for preview
      causalityId
    })

  } catch (error) {
    console.error('Upload error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

