'use client'

import { useState } from 'react'
import { useRouter } from 'next/navigation'
import Link from 'next/link'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Checkbox } from '@/components/ui/checkbox'
import { useToast } from '@/hooks/use-toast'
import { createClient } from '@/lib/supabase/client'
import { Shield, Mail, Lock, User, ArrowRight } from 'lucide-react'

export default function SignUp() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [agreeToTerms, setAgreeToTerms] = useState(false)
  const [loading, setLoading] = useState(false)
  const { toast } = useToast()
  const router = useRouter()
  const supabase = createClient()

  const handleSignUp = async (e: React.FormEvent) => {
    e.preventDefault()

    if (password !== confirmPassword) {
      toast({
        title: 'Password mismatch',
        description: 'Passwords do not match.',
        variant: 'destructive',
      })
      return
    }

    if (!agreeToTerms) {
      toast({
        title: 'Terms required',
        description: 'Please agree to the terms of service.',
        variant: 'destructive',
      })
      return
    }

    setLoading(true)

    try {
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          emailRedirectTo: `${window.location.origin}/dashboard`
        }
      })

      if (error) {
        toast({
          title: 'Sign up failed',
          description: error.message,
          variant: 'destructive',
        })
        return
      }

      if (data.user && !data.session) {
        toast({
          title: 'Check your email',
          description: 'We sent you a confirmation link.',
        })
      } else if (data.session) {
        // Auto-signed in
        toast({
          title: 'Welcome to Foldera!',
          description: 'Your account has been created successfully.',
        })
        router.push('/dashboard')
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'An unexpected error occurred.',
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleSignUp = async () => {
    try {
      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'google',
        options: {
          redirectTo: `${window.location.origin}/dashboard`
        }
      })

      if (error) {
        toast({
          title: 'Sign up failed',
          description: error.message,
          variant: 'destructive',
        })
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to initiate Google sign up.',
        variant: 'destructive',
      })
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background to-muted flex items-center justify-center p-4">
      <div className="w-full max-w-md space-y-6">
        {/* Logo */}
        <div className="text-center">
          <Link href="/" className="inline-flex items-center">
            <Shield className="text-primary text-3xl mr-3" />
            <span className="text-2xl font-bold">Foldera</span>
          </Link>
          <p className="mt-2 text-muted-foreground">
            Create your account
          </p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Get Started</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Google Sign Up */}
            <Button
              variant="outline"
              className="w-full"
              onClick={handleGoogleSignUp}
              data-testid="google-signup-button"
            >
              <svg className="mr-2 h-4 w-4" viewBox="0 0 24 24">
                <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
              Continue with Google
            </Button>

            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <span className="w-full border-t" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-background px-2 text-muted-foreground">
                  Or continue with email
                </span>
              </div>
            </div>

            {/* Email Sign Up Form */}
            <form onSubmit={handleSignUp} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <div className="relative">
                  <Mail className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="email"
                    type="email"
                    placeholder="you@company.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="pl-10"
                    required
                    data-testid="email-input"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="password">Password</Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="password"
                    type="password"
                    placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="pl-10"
                    required
                    minLength={6}
                    data-testid="password-input"
                  />
                </div>
                <p className="text-xs text-muted-foreground">
                  Must be at least 6 characters
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="confirmPassword">Confirm Password</Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="confirmPassword"
                    type="password"
                    placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    className="pl-10"
                    required
                    data-testid="confirm-password-input"
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  id="terms"
                  checked={agreeToTerms}
                  onCheckedChange={(checked) => setAgreeToTerms(checked as boolean)}
                  data-testid="terms-checkbox"
                />
                <Label htmlFor="terms" className="text-sm">
                  I agree to the{' '}
                  <Link href="/terms" className="text-primary hover:underline">
                    Terms of Service
                  </Link>{' '}
                  and{' '}
                  <Link href="/privacy" className="text-primary hover:underline">
                    Privacy Policy
                  </Link>
                </Label>
              </div>

              <Button
                type="submit"
                className="w-full"
                disabled={loading}
                data-testid="signup-submit-button"
              >
                {loading ? (
                  'Creating account...'
                ) : (
                  <>
                    Create Account
                    <ArrowRight className="ml-2 h-4 w-4" />
                  </>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        <div className="text-center">
          <p className="text-sm text-muted-foreground">
            Already have an account?{' '}
            <Link
              href="/auth/signin"
              className="text-primary hover:underline font-medium"
            >
              Sign in
            </Link>
          </p>
        </div>

        <div className="text-center">
          <Link
            href="/"
            className="text-sm text-muted-foreground hover:text-foreground transition-colors"
          >
            ‚Üê Back to home
          </Link>
        </div>
      </div>
    </div>
  )
}

components/FileUpload.tsx.content:
'use client'

import { useState, useCallback } from 'react'
import { useDropzone } from 'react-dropzone'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Progress } from '@/components/ui/progress'
import { useToast } from '@/hooks/use-toast'
import { Upload, FileText, AlertTriangle, CheckCircle, X } from 'lucide-react'
import { createClient } from '@/lib/supabase/client'

interface UploadedFile {
  file: File
  id: string
  status: 'pending' | 'uploading' | 'processing' | 'completed' | 'error'
  progress: number
  error?: string
}

interface FileUploadProps {
  onUploadComplete?: (result: any) => void
  maxFiles?: number
}

export default function FileUpload({ onUploadComplete, maxFiles = 10 }: FileUploadProps) {
  const [files, setFiles] = useState<UploadedFile[]>([])
  const [isUploading, setIsUploading] = useState(false)
  const { toast } = useToast()
  const supabase = createClient()

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const newFiles: UploadedFile[] = acceptedFiles.map(file => ({
      file,
      id: crypto.randomUUID(),
      status: 'pending',
      progress: 0
    }))

    setFiles(prev => [...prev, ...newFiles].slice(0, maxFiles))
  }, [maxFiles])

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxFiles: maxFiles - files.length,
    disabled: isUploading
  })

  const removeFile = (id: string) => {
    setFiles(prev => prev.filter(f => f.id !== id))
  }

  const uploadFiles = async () => {
    if (files.length === 0) return

    setIsUploading(true)

    try {
      // Check authentication
      const { data: { user }, error: authError } = await supabase.auth.getUser()
      if (authError || !user) {
        toast({
          title: 'Authentication required',
          description: 'Please sign in to upload files',
          variant: 'destructive'
        })
        return
      }

      // Update all files to uploading status
      setFiles(prev => prev.map(f => ({ ...f, status: 'uploading' as const, progress: 0 })))

      // Prepare FormData
      const formData = new FormData()
      files.forEach(({ file }) => {
        formData.append('files', file)
      })

      // Simulate progress updates
      const progressInterval = setInterval(() => {
        setFiles(prev => prev.map(f => 
          f.status === 'uploading' 
            ? { ...f, progress: Math.min(f.progress + Math.random() * 20, 90) }
            : f
        ))
      }, 500)

      // Upload to API
      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData
      })

      clearInterval(progressInterval)

      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Upload failed')
      }

      const result = await response.json()

      // Update files to completed status
      setFiles(prev => prev.map(f => ({ 
        ...f, 
        status: 'completed' as const, 
        progress: 100 
      })))

      toast({
        title: 'Upload completed!',
        description: `${result.filesProcessed} files processed. ${result.discrepanciesFound} discrepancies detected.`,
      })

      onUploadComplete?.(result)

      // Clear files after 2 seconds
      setTimeout(() => {
        setFiles([])
      }, 2000)

    } catch (error) {
      console.error('Upload error:', error)

      // Update files to error status
      setFiles(prev => prev.map(f => ({ 
        ...f, 
        status: 'error' as const,
        error: error instanceof Error ? error.message : 'Upload failed'
      })))

      toast({
        title: 'Upload failed',
        description: error instanceof Error ? error.message : 'An unknown error occurred',
        variant: 'destructive'
      })
    } finally {
      setIsUploading(false)
    }
  }

  const getStatusIcon = (status: UploadedFile['status']) => {
    switch (status) {
      case 'pending':
        return <FileText className="h-4 w-4 text-muted-foreground" />
      case 'uploading':
      case 'processing':
        return <Upload className="h-4 w-4 text-blue-500 animate-pulse" />
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />
      case 'error':
        return <AlertTriangle className="h-4 w-4 text-red-500" />
    }
  }

  const getStatusText = (file: UploadedFile) => {
    switch (file.status) {
      case 'pending':
        return 'Ready to upload'
      case 'uploading':
        return `Uploading... ${Math.round(file.progress)}%`
      case 'processing':
        return 'Processing...'
      case 'completed':
        return 'Completed'
      case 'error':
        return file.error || 'Error'
    }
  }

  return (
    <div className="space-y-6" data-testid="file-upload-component">
      {/* Drop Zone */}
      <Card className="border-dashed border-2">
        <CardContent className="p-8">
          <div
            {...getRootProps()}
            className={`text-center cursor-pointer transition-colors ${
              isDragActive ? 'bg-primary/5 border-primary' : 'hover:bg-muted/50'
            }`}
            data-testid="dropzone"
          >
            <input {...getInputProps()} />
            <Upload className="mx-auto h-12 w-12 text-muted-foreground mb-4" />

            {isDragActive ? (
              <div>
                <p className="text-lg font-medium text-primary">Drop files here</p>
                <p className="text-sm text-muted-foreground">Release to add files</p>
              </div>
            ) : (
              <div>
                <p className="text-lg font-medium mb-2">
                  Drag & drop documents here, or{' '}
                  <span className="text-primary">browse</span>
                </p>
                <p className="text-sm text-muted-foreground mb-4">
                  Supports PDF, DOCX, and TXT files (up to {maxFiles} files)
                </p>
                <Button variant="outline" type="button" data-testid="browse-button">
                  Choose Files
                </Button>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* File List */}
      {files.length > 0 && (
        <Card>
          <CardContent className="p-6">
            <div className="space-y-4">
              <h3 className="text-lg font-medium">Files to Upload</h3>

              {files.map(file => (
                <div
                  key={file.id}
                  className="flex items-center justify-between p-3 bg-muted/50 rounded-lg"
                  data-testid={`file-item-${file.id}`}
                >
                  <div className="flex items-center space-x-3 flex-1 min-w-0">
                    {getStatusIcon(file.status)}
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium truncate">
                        {file.file.name}
                      </p>
                      <p className="text-xs text-muted-foreground">
                        {getStatusText(file)} ‚Ä¢ {(file.file.size / 1024 / 1024).toFixed(1)} MB
                      </p>

                      {(file.status === 'uploading' || file.status === 'processing') && (
                        <Progress value={file.progress} className="mt-2 h-1" />
                      )}
                    </div>
                  </div>

                  {file.status === 'pending' && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeFile(file.id)}
                      data-testid={`remove-file-${file.id}`}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  )}
                </div>
              ))}
            </div>

            {files.some(f => f.status === 'pending') && (
              <div className="mt-6 flex justify-center">
                <Button
                  onClick={uploadFiles}
                  disabled={isUploading}
                  className="px-8"
                  data-testid="upload-files-button"
                >
                  <Upload className="mr-2 h-4 w-4" />
                  {isUploading ? 'Processing...' : `Upload ${files.filter(f => f.status === 'pending').length} Files`}
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  )
}

lib/discrepancy.ts.content:
// Discrepancy Detection Engine for MVP Rules

export interface DiscrepancyResult {
  type: 'date' | 'entity' | 'amount'
  fileA: string
  valueA: string
  fileB: string
  valueB: string
  severity: 'low' | 'medium' | 'high' | 'critical'
}

export interface ParsedDocument {
  filename: string
  content: string
  entities: string[]
  dates: string[]
  amounts: string[]
}

// Extract entities (names, organizations) from text
function extractEntities(text: string): string[] {
  // Simple regex patterns for entities
  const patterns = [
    /\b[A-Z][a-z]+ [A-Z][a-z]+\b/g, // Names like "John Smith"
    /\b[A-Z][a-z]+ (?:Inc|LLC|Corp|Corporation|Ltd|Limited)\b/g, // Company names
    /\b[A-Z]{2,}\b/g // Acronyms
  ]

  const entities = new Set<string>()
  patterns.forEach(pattern => {
    const matches = text.match(pattern) || []
    matches.forEach(match => entities.add(match.trim()))
  })

  return Array.from(entities)
}

// Extract dates from text
function extractDates(text: string): string[] {
  const patterns = [
    /\b\d{1,2}\/\d{1,2}\/\d{4}\b/g, // MM/DD/YYYY
    /\b\d{4}\/\d{1,2}\/\d{1,2}\b/g, // YYYY/MM/DD
    /\b\d{1,2}-\d{1,2}-\d{4}\b/g, // MM-DD-YYYY
    /\b\d{4}-\d{1,2}-\d{1,2}\b/g, // YYYY-MM-DD
    /\b(?:January|February|March|April|May|June|July|August|September|October|November|December)\s+\d{1,2},?\s+\d{4}\b/g // Month DD, YYYY
  ]

  const dates = new Set<string>()
  patterns.forEach(pattern => {
    const matches = text.match(pattern) || []
    matches.forEach(match => dates.add(match.trim()))
  })

  return Array.from(dates)
}

// Extract amounts/numbers from text
function extractAmounts(text: string): string[] {
  const patterns = [
    /\$\d{1,3}(?:,\d{3})*(?:\.\d{2})?/g, // Currency: $1,000.00
    /\b\d{1,3}(?:,\d{3})*(?:\.\d{2})?\s*(?:dollars?|USD)\b/gi, // Written currency
    /\b\d+(?:\.\d+)?%/g, // Percentages
    /\b\d{1,3}(?:,\d{3})*(?:\.\d+)?\b/g // Large numbers
  ]

  const amounts = new Set<string>()
  patterns.forEach(pattern => {
    const matches = text.match(pattern) || []
    matches.forEach(match => amounts.add(match.trim()))
  })

  return Array.from(amounts)
}

// Parse document and extract structured data
export function parseDocument(filename: string, content: string): ParsedDocument {
  return {
    filename,
    content,
    entities: extractEntities(content),
    dates: extractDates(content),
    amounts: extractAmounts(content)
  }
}

// Compare two documents for discrepancies
function compareDocuments(docA: ParsedDocument, docB: ParsedDocument): DiscrepancyResult[] {
  const discrepancies: DiscrepancyResult[] = []

  // Date mismatches
  docA.dates.forEach(dateA => {
    docB.dates.forEach(dateB => {
      if (dateA !== dateB && areSimilarDates(dateA, dateB)) {
        discrepancies.push({
          type: 'date',
          fileA: docA.filename,
          valueA: dateA,
          fileB: docB.filename,
          valueB: dateB,
          severity: 'high'
        })
      }
    })
  })

  // Entity mismatches (similar names with different spellings)
  docA.entities.forEach(entityA => {
    docB.entities.forEach(entityB => {
      if (entityA !== entityB && areSimilarEntities(entityA, entityB)) {
        discrepancies.push({
          type: 'entity',
          fileA: docA.filename,
          valueA: entityA,
          fileB: docB.filename,
          valueB: entityB,
          severity: 'medium'
        })
      }
    })
  })

  // Amount mismatches
  docA.amounts.forEach(amountA => {
    docB.amounts.forEach(amountB => {
      if (amountA !== amountB && areSimilarAmounts(amountA, amountB)) {
        discrepancies.push({
          type: 'amount',
          fileA: docA.filename,
          valueA: amountA,
          fileB: docB.filename,
          valueB: amountB,
          severity: 'critical'
        })
      }
    })
  })

  return discrepancies
}

// Helper functions for similarity detection
function areSimilarDates(dateA: string, dateB: string): boolean {
  // Convert dates to standardized format and compare
  const normA = normalizeDateString(dateA)
  const normB = normalizeDateString(dateB)
  return normA === normB && dateA !== dateB
}

function areSimilarEntities(entityA: string, entityB: string): boolean {
  // Simple Levenshtein distance check
  const distance = levenshteinDistance(entityA.toLowerCase(), entityB.toLowerCase())
  const maxLength = Math.max(entityA.length, entityB.length)
  return distance > 0 && distance <= maxLength * 0.3 // 30% similarity threshold
}

function areSimilarAmounts(amountA: string, amountB: string): boolean {
  // Extract numeric values and compare
  const numA = extractNumericValue(amountA)
  const numB = extractNumericValue(amountB)
  if (numA && numB) {
    const diff = Math.abs(numA - numB) / Math.max(numA, numB)
    return diff < 0.05 // 5% difference threshold
  }
  return false
}

// Utility functions
function normalizeDateString(date: string): string {
  // Attempt to parse and normalize date formats
  const parsed = new Date(date)
  return isNaN(parsed.getTime()) ? date : parsed.toISOString().split('T')[0]
}

function extractNumericValue(text: string): number | null {
  const match = text.match(/[\d,]+(?:\.\d+)?/)
  if (match) {
    const cleaned = match[0].replace(/,/g, '')
    const num = parseFloat(cleaned)
    return isNaN(num) ? null : num
  }
  return null
}

function levenshteinDistance(str1: string, str2: string): number {
  const matrix = Array(str2.length + 1).fill(null).map(() => Array(str1.length + 1).fill(null))

  for (let i = 0; i <= str1.length; i++) matrix[0][i] = i
  for (let j = 0; j <= str2.length; j++) matrix[j][0] = j

  for (let j = 1; j <= str2.length; j++) {
    for (let i = 1; i <= str1.length; i++) {
      const indicator = str1[i - 1] === str2[j - 1] ? 0 : 1
      matrix[j][i] = Math.min(
        matrix[j][i - 1] + 1, // insertion
        matrix[j - 1][i] + 1, // deletion
        matrix[j - 1][i - 1] + indicator // substitution
      )
    }
  }

  return matrix[str2.length][str1.length]
}

// Main discrepancy detection function
export function detectDiscrepancies(documents: ParsedDocument[]): DiscrepancyResult[] {
  const allDiscrepancies: DiscrepancyResult[] = []

  // Compare each document pair
  for (let i = 0; i < documents.length; i++) {
    for (let j = i + 1; j < documents.length; j++) {
      const discrepancies = compareDocuments(documents[i], documents[j])
      allDiscrepancies.push(...discrepancies)
    }
  }

  return allDiscrepancies
}

